import React, { useState } from "react";
// import { auth } from "../../firebase";
import { updatePassword, signOut } from "firebase/auth";
import { useNavigate } from "react-router-dom";

const Account = () => {
  const [newPassword, setNewPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  const handlePasswordChange = async () => {
    if (!newPassword) {
      setMessage("Veuillez entrer un nouveau mot de passe.");
      return;
    }

    try {
      const user = auth.currentUser;
      if (user) {
        await updatePassword(user, newPassword);
        setMessage("Mot de passe mis à jour avec succès !");
        setNewPassword("");
      } else {
        setMessage("Vous devez être connecté pour modifier votre mot de passe.");
      }
    } catch (error) {
      setMessage("Erreur : " + error.message);
    }
  };

  const handleLogout = async () => {
    await signOut(auth);
    navigate("/login");
  };

  return (
    <div className="account-container">
      <h1>Gérer votre compte</h1>
      {message && <p>{message}</p>}
      <div>
        <label>Modifier votre mot de passe :</label>
        <input
          type="password"
          placeholder="Nouveau mot de passe"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
        <button onClick={handlePasswordChange}>Mettre à jour</button>
      </div>
      <div>
        <button onClick={handleLogout}>Se déconnecter</button>
      </div>
    </div>
  );
};

export default Account;
