import React, { useState, useEffect } from "react";
import { auth } from "../firebase"; // Assure-toi que Firebase est bien configuré
import { updatePassword, signOut } from "firebase/auth";
import { useNavigate } from "react-router-dom";

const Account = () => {
  const [user, setUser] = useState(null);
  const [newPassword, setNewPassword] = useState("");
  const [message, setMessage] = useState("");
  const navigate = useNavigate();

  useEffect(() => {
    // Récupérer les informations de l'utilisateur actuel
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      if (currentUser) {
        setUser(currentUser);
      } else {
        navigate("/login"); // Redirige si l'utilisateur n'est pas connecté
      }
    });

    return () => unsubscribe(); // Nettoyer l'écouteur lors du démontage
  }, [navigate]);

  const handlePasswordChange = async () => {
    if (!newPassword) {
      setMessage("Veuillez entrer un nouveau mot de passe.");
      return;
    }

    try {
      await updatePassword(auth.currentUser, newPassword);
      setMessage("Mot de passe mis à jour avec succès !");
      setNewPassword("");
    } catch (error) {
      setMessage("Erreur lors de la mise à jour du mot de passe : " + error.message);
    }
  };

  const handleLogout = async () => {
    await signOut(auth);
    navigate("/login"); // Redirige vers la page de connexion après la déconnexion
  };

  return (
    <div className="account-container">
      <h1>Mon compte</h1>
      {user && (
        <div>
          <p><strong>Nom :</strong> {user.displayName || "Utilisateur"}</p>
          <p><strong>Email :</strong> {user.email}</p>
        </div>
      )}

      <div className="account-section">
        <h2>Modifier le mot de passe</h2>
        <input
          type="password"
          placeholder="Nouveau mot de passe"
          value={newPassword}
          onChange={(e) => setNewPassword(e.target.value)}
        />
        <button onClick={handlePasswordChange}>Mettre à jour</button>
      </div>

      <div className="account-section">
        <button onClick={handleLogout} className="logout-button">
          Se déconnecter
        </button>
      </div>

      {message && <p className="message">{message}</p>}
    </div>
  );
};

export default Account;
