import React, {useState, useEffect} from 'react';
import { Link, Navigate, useNavigate } from "react-router-dom";
import { auth, signInWithEmailAndPassword , signInWithGoogle} from '../firebase';
import './LoginForm.css';
import { FaUser, FaLock, FaPlus } from "react-icons/fa";



const LoginForm = () => {
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [error, setError] = useState("");
  
    const handleLogin = async (e) => {
      e.preventDefault();
      try {
        await signInWithEmailAndPassword(auth, email, password);
        alert("Connexion réussie !");
        setEmail("");
        setPassword("");
      } catch (err) {
        setError(err.message);
      }
    };
    
    const handleGoogleLogin = async () => {
        try {
          const user = await signInWithGoogle();
          console.log("Connexion réussie avec Google :", user);
          alert("Connexion réussie !");
        } catch (error) {
          setError("Erreur lors de la connexion avec Google.");
        }
      };
    
      useEffect(() => {
        // Récupérer les informations de l'utilisateur actuel
        const unsubscribe = auth.onAuthStateChanged((currentUser) => {
          if (currentUser) {
            navigate("/Account");
          }
        });
    
        return () => unsubscribe(); // Nettoyer l'écouteur lors du démontage
      }, [navigate]);


    return(

        <>
        {/* En tete du site  */}
    <header>   
        <nav>
        <div className='logo'><Link to ='/'>Gameshop</Link></div>
            <ul className='nav-links'>
                <li className='UserLogin'><Link to="/login"> <FaUser/></Link></li>
                <li className='Product-list'><Link to ="/product-form"><FaPlus /></Link></li>
                <div className='cart-icon'>
                    <span className='cart-count'>0</span>
                    🛒
                </div>          
            </ul>
        </nav>
    </header>
    
        <div className='wrapper'>
            <h1>Login</h1>
            {error && <p style={{ color: "red" }}>{error}</p>}
            <form onSubmit={handleLogin}>
                <div className='input-box'>
                
                    <input 
                    type="email" 
                    placeholder='email' 
                    value={email} 
                    onChange={(e) => setEmail(e.target.value)}
                    required />
                    <FaUser className='icon' />
                </div>

                <div className='input-box'>
                    <input type="password" 
                    placeholder='Password'
                    value={password}
                    onChange={(e) => setPassword(e.target.value)} 
                    required />
                    <FaLock className='icon'/>
                </div>
                

                <div className='remember-forgot'>
                    <label> <input type="checkbox" /> Remember me </label>
                    <a href="#">Forgot password ?</a>
                </div>

                <button type='submit'>Login</button>

                <div className='register-link'>
                    <p> Don't have an account ? <Link to = "/signup">Register</Link></p>

                </div>

            </form>
        </div>        
        </>

    )
};

export default LoginForm;
