import { doc, setDoc } from "firebase/firestore";
import React, { useState } from "react";
import { auth, db } from "./firebase";

const AdminSetup = () => {
  const [message, setMessage] = useState("");

  const setupAdmin = async () => {
    try {
      const user = auth.currentUser;
      if (!user) {
        setMessage("Vous devez être connecté");
        return;
      }

      // Créer ou mettre à jour le document utilisateur
      await setDoc(
        doc(db, "users", user.uid),
        {
          email: user.email,
          role: "admin",
          createdAt: new Date().toISOString(),
        },
        { merge: true }
      );

      setMessage("Compte admin créé avec succès!");
    } catch (error) {
      console.error("Error:", error);
      setMessage("Erreur lors de la création du compte admin");
    }
  };

  return (
    <div style={{ padding: "20px" }}>
      <h2>Configuration Admin</h2>
      <button onClick={setupAdmin}>Devenir Admin</button>
      {message && <p>{message}</p>}
    </div>
  );
};

export default AdminSetup;
