import React, { useEffect, useState } from "react";
import axios from "axios";
import '/Users/Berud/Desktop/test/frontend/src/index.css';
import './ProductList.css'
import { FaUser, FaLock, FaPlus } from "react-icons/fa";
import { Link } from "react-router-dom";


const ProductList = () => {
  const [products, setProducts] = useState([]); // Ã‰tat pour gÃ©rer la liste des produits

  // Charger les produits lors du montage du composant
  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get("http://localhost:3000/products");
        setProducts(response.data); // Met Ã  jour l'Ã©tat avec les produits rÃ©cupÃ©rÃ©s
      } catch (error) {
        console.error("Erreur lors du chargement des produits :", error.message);
      }
    };

    fetchProducts();
  }, []);

  // Supprimer un produit
  const handleDelete = async (productId) => {
    try {
      const response = await axios.delete(`http://localhost:3000/products/${productId}`);
      if (response.status === 200) {
        alert("Produit supprimÃ© avec succÃ¨s !");
        // Met Ã  jour l'Ã©tat aprÃ¨s la suppression
        setProducts(products.filter((product) => product.id !== productId));
      }
    } catch (error) {
      console.error("Erreur lors de la suppression du produit :", error.message);
    }
  };

  return (
    <>
    {/* En tete du site  */}
    <header>   
        <nav>
            <div className='logo'>Gameshop</div>
            <ul className='nav-links'>
                <li><Link to="/">Acceuil</Link></li>   
                <li className='UserLogin'><Link to="/login"> <FaUser/></Link></li>
                <li className='Product-list'><Link to ="/product-form"><FaPlus /></Link></li>
                <div className='cart-icon'>
                    <span className='cart-count'>0</span>
                    ðŸ›’
                </div>          
            </ul>
        </nav>
    </header>
    <div className="product-list-container">
      <h3>Produits disponibles :</h3>
      {products.length > 0 ? (
        <ul className="product-list">
          {products.map((product) => (
            <li key={product.id} className="product-item">
              <div className="product-details">
                <strong>{product.name}</strong> - {product.price}â‚¬<br />
                <p>{product.categorie}</p>
                <p>{product.description}</p>
              </div>

              <button
                onClick={() => handleDelete(product.id)}
                className="delete-button"
              >
                Supprimer
              </button>
            </li>
          ))}
        </ul>
      ) : (
        <p>Aucun produit disponible.</p>
      )}
    </div>   
    </>

  );
};

export default ProductList;
