import { Link } from "react-router-dom";
import './Home.css';
import '/Users/Berud/Desktop/test/frontend/src/index.css';
import { FaUser, FaLock, FaPlus } from "react-icons/fa";
import axios from "axios";
const Home = () => {
    const [categorie, setCategorie] = useState([]); // etat pour gerer les categories

    useEffect(() => {
        // Charger les categorie depuis le backend
        const fetchCategories = async () => {
            try{
                const reponse = await axios.get("http://localhost:3000/categories");
                setCategorie(reponse.data); // met a jour les categories
            } catch (error) {
                console.error("Erreur lors de la rÃ©cupÃ©ration des catÃ©gories :", error.message);
            }
        };

        fetchCategories();
    }, []);

    return (
    <> 
            {/* En tete du site  */}
    {/* <header>   
        <nav>
            <div className='logo'><Link to ='/'>Gameshop</Link></div>
            <ul className='nav-links'>
                <li className='UserLogin'><Link to="/login"> <FaUser/></Link></li>
                <li className='Product-list'><Link to ="/product-form"><FaPlus /></Link></li>
                <div className='cart-icon'>
                    <span className='cart-count'>0</span>
                    ðŸ›’
                </div>          
            </ul>
        </nav>
    </header> */}

    <main className='games-selection'>
        <section className='hero-section'>
            <h1>GameShop</h1>
            <p> Find a account for you prefered game </p>
        </section>

        {/* Sidebar sur le coter gauche et le main content */}
        <div className='layout-container'>

            {/* Sidebar */}
            <aside className='game-sidebar'>
                <div className='sidebar-content'>
                    <h3>All games</h3>
                        <ul className='game-list'>
                        {categorie.length > 0 ? (
                            categorie.map((categorie, index) => (
                                <li key={index}>
                                    <Link to ={''}>{categorie}</Link>
                                </li>
                            ))
                        )   : (
                            <li>Aucune categorie disponible.</li>
                        )}
                        </ul>
                </div>
            </aside>

            {/* Main Content */}
            <div className='games-grid'> 
                {/* jjk */}
                <a href="" className='game-card'>
                    <div className='game-banner'>
                        <img src={"https://malboro18games.com/cdn/shop/files/120300.png?v=1736908124&width=823"} alt="Jujutsu Kaisen"/>
                    </div>
                    <div className='game-info'>
                        <h2> Jujutsu-Kaisen phantom parade</h2>
                    </div>                   
                </a>

                {/* Dragon ball legend */}
                <a href="" className='game-card '>
                    <div className='game-banner'>
                        <img src={"//malboro18games.com/cdn/shop/files/dragonballlegends_ea8878c7-4eb4-46c9-8506-ef47d1a3a5da.png?v=1726339726&width=550 550w,//malboro18games.com/cdn/shop/files/dragonballlegends_ea8878c7-4eb4-46c9-8506-ef47d1a3a5da.png?v=1726339726 831w"} alt="Db legend" />
                    </div>
                    <div className='game-info'>
                        <h2>Db legend</h2>
                    </div>
                </a>

                <a href=""></a>



            </div>


        </div>

    </main>

    

        </>    
    )      

};

