import { addDoc, collection } from "firebase/firestore";
import React, { useEffect, useRef, useState } from "react";
import { db } from "../firebase";
import NavBar from "../NavBar/NavBar";
import "./ProductForm.css";

const ProductForm = () => {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [categorie, setCategorie] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [server, setServer] = useState("");
  const [subProduct, setSubProduct] = useState([{ dlc: "", price: "" }]);

  // État pour la suggestion de catégories
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const inputRef = useRef(null);

  // Liste initiale des catégories
  const initialCategories = [
    "Jujutsu Kaisen Phantom Parade",
    "Genshin Impact",
    "Honkai: Star Rail",
    "Fate/Grand Order",
    "Dragon Ball Legend",
    "Pokemon TCG Pocket",
    "Yu-Gi-Oh! Duel Links",
    "One Piece Bounty Rush",
    "Dokkan Battle",
  ];

  // Fonction de filtrage des suggestions
  const filterSuggestions = (input) => {
    if (!input) {
      setSuggestions([]);
      setShowSuggestions(false);
      return;
    }
    const filtered = initialCategories.filter((category) =>
      category.toLowerCase().includes(input.toLowerCase())
    );
    setSuggestions(filtered);
    setShowSuggestions(filtered.length > 0);
  };

  // Gérer le changement de catégorie (saisie utilisateur)
  const handleCategorieChange = (e) => {
    const value = e.target.value;
    setCategorie(value);
    filterSuggestions(value);
  };

  // Sélectionner une suggestion
  const handleSelectSuggestion = (suggestion) => {
    setCategorie(suggestion);
    setShowSuggestions(false);
  };

  // Fermer les suggestions quand on clique ailleurs
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (inputRef.current && !inputRef.current.contains(event.target)) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Ajouter un sous-produit
  const addSubProduct = () => {
    setSubProduct([...subProduct, { dlc: "", price: "" }]);
  };

  // Supprimer un sous-produit
  const deleteSubProduct = (index) => {
    setSubProduct(subProduct.filter((_, i) => i !== index));
  };

  // Mettre à jour un sous-produit
  const updateSubProduct = (index, field, value) => {
    const updatedSubProduct = subProduct.map((sub, i) =>
      i === index ? { ...sub, [field]: value } : sub
    );
    setSubProduct(updatedSubProduct);
  };

  const [formData, setFormData] = useState({
    name: "",
    price: "",
    description: "",
    imageUrl: "",
    categoryId: "",
    createdAt: new Date(), // Ajout d'un timestamp pour le tri
  });

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Ajout du produit dans Firestore
      const productsRef = collection(db, "products");
      await addDoc(productsRef, {
        ...formData,
        price: Number(formData.price), // Conversion en nombre
      });

      // Réinitialisation du formulaire
      setFormData({
        name: "",
        price: "",
        description: "",
        imageUrl: "",
        categoryId: "",
        createdAt: new Date(),
      });

      alert("Produit ajouté avec succès!");
    } catch (error) {
      console.error("Erreur lors de l'ajout du produit:", error);
      alert("Erreur lors de l'ajout du produit");
    }
  };

  return (
    <>
      <NavBar />
      <h2>Add New Product</h2>
      <div className="container">
        <form onSubmit={handleSubmit} className="product-form">
          <div className="form-group">
            <label htmlFor="product-name">Name:</label>
            <input
              type="text"
              id="product-name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter product name"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="product-server">Server (optional):</label>
            <select
              id="product-server"
              value={server}
              onChange={(e) => setServer(e.target.value)}
            >
              <option value="">Select a server</option>
              <option value="America">America</option>
              <option value="Europe">Europe</option>
              <option value="Asia">Asia</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="product-price">Price:</label>
            <input
              type="number"
              id="product-price"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              placeholder="Enter price"
              min="0"
              step="0.01"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="product-categorie">Game:</label>
            <input
              type="text"
              id="product-categorie"
              value={categorie}
              onChange={(e) => setCategorie(e.target.value)}
              placeholder="Enter game name"
              required
            />
          </div>

          {/* Section des versions (anciennement subProduct) */}
          <div className="form-group">
            <label>Versions:</label>
            {subProduct.map((sub, index) => (
              <div key={index} className="sub-product-form">
                <input
                  type="text"
                  placeholder="Version name"
                  value={sub.dlc}
                  onChange={(e) =>
                    updateSubProduct(index, "dlc", e.target.value)
                  }
                />
                <input
                  type="number"
                  placeholder="Version price"
                  value={sub.price}
                  onChange={(e) =>
                    updateSubProduct(index, "price", e.target.value)
                  }
                  min="0"
                  step="0.01"
                />
                <button type="button" onClick={() => deleteSubProduct(index)}>
                  Delete
                </button>
              </div>
            ))}
            <button type="button" onClick={addSubProduct}>
              Add Version
            </button>
          </div>

          <div className="form-group">
            <label htmlFor="product-image">Image URL:</label>
            <input
              type="url"
              id="product-image"
              value={imageUrl}
              onChange={(e) => setImageUrl(e.target.value)}
              placeholder="Enter image URL"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="product-description">Description:</label>
            <textarea
              id="product-description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Enter product description"
              rows="4"
              required
            />
          </div>

          <button type="submit" className="submit-button">
            Add Product
          </button>
        </form>
      </div>
    </>
  );
};

export default ProductForm;
