import {
  addDoc,
  collection,
  deleteDoc,
  doc,
  getDocs,
  setDoc,
  updateDoc,
} from "firebase/firestore";
import React, { useContext, useEffect, useState } from "react";
import { FaEdit, FaTrash } from "react-icons/fa";
import { AuthContext } from "../context/AuthContext";
import { db } from "../firebase";
import NavBar from "../NavBar/NavBar";
import "./Dashboard.css";

const Dashboard = () => {
  const { isAdmin } = useContext(AuthContext);
  const [products, setProducts] = useState([]);
  const [categories, setCategories] = useState([]);
  const [selectedCategory, setSelectedCategory] = useState(null);
  const [editingProduct, setEditingProduct] = useState(null);

  useEffect(() => {
    if (isAdmin) {
      fetchCategories();
    }
  }, [isAdmin]);

  const fetchCategories = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "categories"));
      const categoriesData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      console.log("Categories fetched:", categoriesData);
      setCategories(categoriesData);
    } catch (error) {
      console.error("Error fetching categories:", error);
    }
  };

  const fetchProducts = async (categoryId) => {
    try {
      const querySnapshot = await getDocs(collection(db, "products"));
      const productsData = querySnapshot.docs
        .map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }))
        .filter((product) =>
          categoryId ? product.categoryId === categoryId : true
        );
      setProducts(productsData);
    } catch (error) {
      console.error("Error fetching products:", error);
    }
  };

  const handleEdit = (product) => {
    setEditingProduct(product);
  };

  const handleUpdate = async (e) => {
    e.preventDefault();
    try {
      const productRef = doc(db, "products", editingProduct.id);
      await updateDoc(productRef, {
        name: editingProduct.name,
        price: editingProduct.price,
        description: editingProduct.description,
        // Ajoutez d'autres champs selon vos besoins
      });

      setEditingProduct(null);
      fetchProducts(selectedCategory); // Rafraîchir la liste
      alert("Product updated successfully!");
    } catch (error) {
      console.error("Error updating product:", error);
      alert("Error updating product");
    }
  };

  const handleDelete = async (productId) => {
    if (window.confirm("Are you sure you want to delete this product?")) {
      try {
        await deleteDoc(doc(db, "products", productId));
        setProducts(products.filter((p) => p.id !== productId));
        alert("Product deleted successfully!");
      } catch (error) {
        console.error("Error deleting product:", error);
        alert("Error deleting product");
      }
    }
  };

  const makeUserAdmin = async (userId) => {
    try {
      await setDoc(
        doc(db, "users", userId),
        {
          role: "admin",
        },
        { merge: true }
      );
      alert("User is now an admin!");
    } catch (error) {
      console.error("Error updating user role:", error);
      alert("Error making user admin");
    }
  };

  const addCategory = async () => {
    try {
      const categoriesRef = collection(db, "categories");
      await addDoc(categoriesRef, {
        name: "Jeux d'action",
        imageUrl: "https://example.com/action-games.jpg",
        description: "Des jeux d'action palpitants",
      });

      await addDoc(categoriesRef, {
        name: "Jeux de stratégie",
        imageUrl: "https://example.com/strategy-games.jpg",
        description: "Des jeux de stratégie captivants",
      });

      await addDoc(categoriesRef, {
        name: "RPG",
        imageUrl: "https://example.com/rpg-games.jpg",
        description: "Des jeux de rôle immersifs",
      });

      fetchCategories(); // Rafraîchir la liste
      alert("Categories added successfully!");
    } catch (error) {
      console.error("Error adding categories:", error);
      alert("Error adding categories");
    }
  };

  return (
    <div className="dashboard">
      <NavBar />
      <div className="dashboard-container">
        <h1>Dashboard</h1>
        <div className="dashboard-content">
          {!isAdmin ? (
            <div>Accès non autorisé</div>
          ) : (
            <div className="admin-section">
              <div className="categories-section">
                <div className="section-header">
                  <h2>Catégories</h2>
                  <button onClick={addCategory} className="add-btn">
                    Ajouter des catégories
                  </button>
                </div>
                <div className="products-grid">
                  {categories.map((category) => (
                    <div
                      key={category.id}
                      className={`product-card ${
                        selectedCategory === category.id ? "selected" : ""
                      }`}
                      onClick={() => {
                        setSelectedCategory(category.id);
                        fetchProducts(category.id);
                      }}
                      style={{ cursor: "pointer" }}
                    >
                      <div className="product-image">
                        <img
                          src={category.imageUrl || "/default-category.jpg"}
                          alt={category.name}
                        />
                      </div>
                      <div className="product-details">
                        <h2>{category.name}</h2>
                        {category.description && (
                          <p className="description">{category.description}</p>
                        )}
                      </div>
                    </div>
                  ))}
                </div>
              </div>

              {selectedCategory && (
                <div className="products-section">
                  <h2>Produits de la catégorie</h2>
                  {editingProduct ? (
                    <form onSubmit={handleUpdate} className="edit-form">
                      <div className="form-group">
                        <label>Nom:</label>
                        <input
                          type="text"
                          value={editingProduct.name}
                          onChange={(e) =>
                            setEditingProduct({
                              ...editingProduct,
                              name: e.target.value,
                            })
                          }
                        />
                      </div>

                      <div className="form-group">
                        <label>Prix:</label>
                        <input
                          type="number"
                          value={editingProduct.price}
                          onChange={(e) =>
                            setEditingProduct({
                              ...editingProduct,
                              price: e.target.value,
                            })
                          }
                        />
                      </div>

                      <div className="form-group">
                        <label>Description:</label>
                        <textarea
                          value={editingProduct.description}
                          onChange={(e) =>
                            setEditingProduct({
                              ...editingProduct,
                              description: e.target.value,
                            })
                          }
                        />
                      </div>

                      <div className="button-group">
                        <button type="submit">Save</button>
                        <button
                          type="button"
                          onClick={() => setEditingProduct(null)}
                        >
                          Cancel
                        </button>
                      </div>
                    </form>
                  ) : (
                    <div className="products-grid">
                      {products.map((product) => (
                        <div key={product.id} className="product-card">
                          <div className="product-image">
                            <img
                              src={product.imageUrl || "/default-product.jpg"}
                              alt={product.name}
                            />
                          </div>
                          <div className="product-details">
                            <h2>{product.name}</h2>
                            <p className="price">${product.price}</p>
                            {product.subProduct &&
                              product.subProduct.length > 0 && (
                                <div className="sub-products">
                                  <h3>Associated Products:</h3>
                                  <ul>
                                    {product.subProduct.map((sub, index) => (
                                      <li key={index}>
                                        {sub.dlc} - ${sub.price}
                                      </li>
                                    ))}
                                  </ul>
                                </div>
                              )}
                          </div>
                          <div className="product-actions">
                            <button
                              onClick={() => handleEdit(product)}
                              className="edit-btn"
                            >
                              <FaEdit /> Edit
                            </button>
                            <button
                              onClick={() => handleDelete(product.id)}
                              className="delete-btn"
                            >
                              <FaTrash /> Delete
                            </button>
                          </div>
                        </div>
                      ))}
                    </div>
                  )}
                </div>
              )}
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
