import React, { useEffect, useState } from "react";
import "../../index.css";
import NavBar from "../NavBar/NavBar.jsx";
import "./Home.css";

const Home = () => {
  const [latestProducts, setLatestProducts] = useState([]);

  useEffect(() => {
    fetchLatestProducts();
  }, []);

  const fetchLatestProducts = async () => {
    try {
      const response = await fetch("http://localhost:3000/api/products/latest");
      if (!response.ok) {
        throw new Error("Erreur lors de la récupération des produits");
      }
      const data = await response.json();
      console.log("Produits récupérés:", data);
      setLatestProducts(data);
    } catch (error) {
      console.error("Erreur:", error);
    }
  };

  // Fonction pour ajouter des produits de test
  const addTestProducts = async () => {
    try {
      const productsRef = collection(db, "products");

      const testProducts = [
        {
          name: "The Legend of Zelda",
          price: 59.99,
          description: "Une aventure épique dans un monde ouvert",
          imageUrl: "https://example.com/zelda.jpg",
          categoryId: "action-adventure",
        },
        {
          name: "Final Fantasy XVI",
          price: 69.99,
          description: "Le dernier opus de la série Final Fantasy",
          imageUrl: "https://example.com/ff16.jpg",
          categoryId: "rpg",
        },
        {
          name: "FIFA 24",
          price: 49.99,
          description: "Le meilleur jeu de football",
          imageUrl: "https://example.com/fifa24.jpg",
          categoryId: "sports",
        },
        {
          name: "Cyberpunk 2077",
          price: 39.99,
          description: "Une aventure futuriste dans Night City",
          imageUrl: "https://example.com/cyberpunk.jpg",
          categoryId: "rpg",
        },
      ];

      for (const product of testProducts) {
        await addDoc(productsRef, product);
      }

      alert("Produits de test ajoutés avec succès!");
      fetchLatestProducts(); // Rafraîchir la liste
    } catch (error) {
      console.error("Erreur lors de l'ajout des produits de test:", error);
    }
  };

  return (
    <div>
      <NavBar />
      <main>
        <div className="hero-section">
          <h1>GameShop</h1>
          <p>Find every you want</p>
        </div>

        <section className="latest-products">
          <h2>Derniers Produits</h2>
          {/* Bouton temporaire pour ajouter des produits de test */}
          <button onClick={addTestProducts} className="add-test-products">
            Ajouter des produits de test
          </button>
          <div className="products-grid">
            {latestProducts.map((product) => (
              <div key={product.id} className="product-card">
                <div className="product-image">
                  <img
                    src={product.imageUrl || "/default-product.jpg"}
                    alt={product.name}
                  />
                </div>
                <div className="product-details">
                  <h3>{product.name}</h3>
                  <p className="price">${product.price}</p>
                  <p className="description">{product.description}</p>
                </div>
              </div>
            ))}
          </div>
        </section>
      </main>
    </div>
  );
};

export default Home;
