import { PayPalButtons } from "@paypal/react-paypal-js";
import axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import NavBar from "../NavBar/NavBar";
import "./ProductDetails.css";

const ProductDetails = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedSubProduct, setSelectedSubProduct] = useState(null);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3000/products/${productId}`
        );
        setProduct(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Erreur lors de la récupération du produit:", error);
        setLoading(false);
      }
    };

    fetchProduct();
  }, [productId]);

  const handleAddToCart = () => {
    const itemToAdd = {
      ...product,
      subProduct: selectedSubProduct
        ? [selectedSubProduct]
        : product.subProduct,
    };
    addToCart(itemToAdd);
    alert("Product added to cart!");
  };

  const handlePaypalApprove = (data, actions) => {
    return actions.order.capture().then(function (details) {
      alert("Transaction completed by " + details.payer.name.given_name);
      // Ici vous pouvez ajouter la logique post-paiement
    });
  };

  const createPaypalOrder = (data, actions) => {
    return actions.order.create({
      purchase_units: [
        {
          description: product.name,
          amount: {
            value: selectedSubProduct
              ? selectedSubProduct.price
              : product.price,
          },
          payee: {
            email_address: "ayoube.manjal.pro@gmail.com", // Email PayPal du vendeur
          },
        },
      ],
    });
  };

  const handleBuyNow = () => {
    // Créer un bouton PayPal temporaire et cliquer dessus
    const paypalButtons = document.querySelector(".paypal-buttons");
    if (paypalButtons) {
      paypalButtons.click();
    }
  };

  if (loading) {
    return <div className="loading">Chargement...</div>;
  }

  if (!product) {
    return <div>Produit non trouvé</div>;
  }

  return (
    <div className="product-details-page">
      <NavBar />
      <div className="product-details-container">
        <div className="product-image-section">
          <img src={product.imageUrl} alt={product.name} />
        </div>
        <div className="product-info-section">
          <h1>{product.name}</h1>

          <p className="price">${product.price}USD</p>

          <div className="shipping-info">
            <a href="/shipping">Shipping</a> calculated at checkout.
          </div>

          <div className="payment-options">
            <PayPalButtons
              createOrder={createPaypalOrder}
              onApprove={handlePaypalApprove}
              style={{
                layout: "horizontal",
                color: "gold",
                shape: "rect",
                label: "pay",
              }}
            />

            <button className="add-to-cart-btn" onClick={handleAddToCart}>
              Add to Cart
            </button>

            <button className="buy-now-btn" onClick={handleBuyNow}>
              Buy Now with PayPal
            </button>
          </div>

          {product.subProduct && product.subProduct.length > 0 && (
            <div className="sub-products-section">
              <h2>Available Options</h2>
              <div className="sub-products-list">
                {product.subProduct.map((sub, index) => (
                  <div
                    key={index}
                    className={`sub-product-option ${
                      selectedSubProduct === sub ? "selected" : ""
                    }`}
                    onClick={() => setSelectedSubProduct(sub)}
                  >
                    <span>{sub.dlc}</span>
                    <span className="sub-price">${sub.price}</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          <div className="description">
            <h2>Description</h2>
            <p>{product.description}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
