import React, { useEffect, useState } from "react";
import NavBar from "../NavBar/NavBar";
import "./Home.css";
import { collection, getDocs, query, orderBy, limit } from "firebase/firestore";
import { db } from "../../components/firebase";

const Home = () => {
  const [recentProducts, setRecentProducts] = useState([]);

  useEffect(() => {
    const fetchRecentProducts = async () => {
      const q = query(
        collection(db, "products"),
        orderBy("createdAt", "desc"),
        limit(8)
      );

      const querySnapshot = await getDocs(q);
      const products = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
      }));
      setRecentProducts(products);
    };

    fetchRecentProducts();
  }, []);

  return (
    <div className="home">
      <NavBar />
      <div className="hero-section">
        <h1>Bienvenue sur notre boutique de jeux</h1>
        <p>
          Découvrez notre sélection de comptes et d'items pour vos jeux préférés
        </p>
      </div>

      <section className="recent-products-section">
        <h2>Derniers produits ajoutés</h2>
        <div className="recent-products-grid">
          {recentProducts.map((product) => (
            <div key={product.id} className="product-card">
              <div className="product-image">
                <img
                  src={product.imageUrl || "/default-product.png"}
                  alt={product.name}
                />
              </div>
              <div className="product-info">
                <h3>{product.name}</h3>
                <p className="product-category">{product.categorie}</p>
                <p className="product-price">{product.price} €</p>
              </div>
            </div>
          ))}
        </div>
      </section>
    </div>
  );
};

export default Home;
