import {
  collection,
  deleteDoc,
  doc,
  getDocs,
  orderBy,
  query,
} from "firebase/firestore";
import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import migrateToFirestore from "../../utils/migrateToFirestore";
import { AuthContext } from "../context/AuthContext";
import { db } from "../firebase";
import NavBar from "../NavBar/NavBar";
import "./Dashboard.css";

const Dashboard = () => {
  const { isAdmin } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [activeTab, setActiveTab] = useState("products");
  const navigate = useNavigate();

  useEffect(() => {
    if (isAdmin) {
      if (activeTab === "orders") {
        fetchOrders();
      } else if (activeTab === "products") {
        fetchProducts();
      }
    }
  }, [isAdmin, activeTab]);

  const fetchOrders = async () => {
    try {
      const ordersRef = collection(db, "orders");
      const q = query(ordersRef, orderBy("date", "desc"));
      const querySnapshot = await getDocs(q);

      const ordersData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
        date: doc.data().date.toDate().toLocaleString(),
      }));

      setOrders(ordersData);
    } catch (error) {
      console.error("Erreur lors de la récupération des commandes:", error);
    }
  };

  const fetchProducts = async () => {
    try {
      const productsRef = collection(db, "products");
      const querySnapshot = await getDocs(productsRef);
      const productsData = querySnapshot.docs.map((doc) => {
        const data = doc.data();
        // S'assurer que toutes les propriétés nécessaires existent
        return {
          id: doc.id,
          name: data.name || "",
          price: data.price || 0,
          description: data.description || "",
          categorie: data.categorie || "",
          imageUrl: data.imageUrl || "",
          server: data.server || "",
          subProduct: Array.isArray(data.subProduct) ? data.subProduct : [],
          createdAt: data.createdAt || new Date(),
        };
      });
      console.log("Produits récupérés:", productsData);
      setProducts(productsData);
    } catch (error) {
      console.error("Erreur lors de la récupération des produits:", error);
    }
  };

  const handleMigration = async () => {
    if (
      window.confirm("Voulez-vous migrer tous les produits vers Firestore?")
    ) {
      const success = await migrateToFirestore();
      if (success) {
        alert("Migration réussie!");
      } else {
        alert("Erreur lors de la migration");
      }
    }
  };

  const handleDelete = async (productId) => {
    if (!productId) {
      console.error("ID du produit manquant");
      return;
    }

    if (window.confirm("Êtes-vous sûr de vouloir supprimer ce produit ?")) {
      try {
        const productRef = doc(db, "products", productId);

        // Supprimer le document
        await deleteDoc(productRef);

        // Rafraîchir la liste complète des produits
        fetchProducts();

        console.log("Produit supprimé avec succès, ID:", productId);
        alert("Produit supprimé avec succès!");
      } catch (error) {
        console.error("Erreur détaillée lors de la suppression:", error);
        alert("Erreur lors de la suppression du produit");
      }
    }
  };

  const handleEdit = (productId) => {
    navigate(`/edit-product/${productId}`);
  };

  const renderContent = () => {
    switch (activeTab) {
      case "orders":
        return (
          <div className="orders-section">
            <h2>Historique des Commandes</h2>
            <div className="orders-list">
              {orders.length === 0 ? (
                <p className="no-orders">Aucune commande trouvée</p>
              ) : (
                <table className="orders-table">
                  <thead>
                    <tr>
                      <th>Date</th>
                      <th>Client</th>
                      <th>Produits</th>
                      <th>Total</th>
                      <th>Statut</th>
                    </tr>
                  </thead>
                  <tbody>
                    {orders.map((order) => (
                      <tr key={order.id}>
                        <td>{order.date}</td>
                        <td>{order.userEmail}</td>
                        <td>
                          <ul className="products-list">
                            {order.products.map((product, index) => (
                              <li key={index}>
                                {product.name} x{product.quantity}
                              </li>
                            ))}
                          </ul>
                        </td>
                        <td>${order.total.toFixed(2)}</td>
                        <td>
                          <span
                            className={`status ${order.status.toLowerCase()}`}
                          >
                            {order.status}
                          </span>
                        </td>
                      </tr>
                    ))}
                  </tbody>
                </table>
              )}
            </div>
          </div>
        );
      case "products":
        return (
          <div className="products-section">
            <h2>Gestion des Produits</h2>
            <div className="products-grid">
              {products.map((product) => (
                <div key={product.id} className="product-card">
                  <div className="product-image">
                    <img
                      src={product.imageUrl || "/default-product.jpg"}
                      alt={product.name}
                    />
                  </div>
                  <div className="product-details">
                    <h3>{product.name}</h3>
                    <p className="price">${product.price}</p>
                    <p className="description">{product.description}</p>
                    <p className="category">Catégorie: {product.categorie}</p>
                  </div>
                  <div className="product-actions">
                    <button
                      className="edit-btn"
                      onClick={() => handleEdit(product.id)}
                    >
                      Modifier
                    </button>
                    <button
                      className="delete-btn"
                      onClick={() => handleDelete(product.id)}
                    >
                      Supprimer
                    </button>
                  </div>
                </div>
              ))}
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="dashboard">
      <NavBar />
      <div className="dashboard-container">
        <h1>Dashboard</h1>
        {!isAdmin ? (
          <div className="unauthorized">Accès non autorisé</div>
        ) : (
          <div className="dashboard-layout">
            <div className="sidebar">
              <button
                className={`tab-button ${
                  activeTab === "orders" ? "active" : ""
                }`}
                onClick={() => setActiveTab("orders")}
              >
                Historique des commandes
              </button>
              <button
                className={`tab-button ${
                  activeTab === "products" ? "active" : ""
                }`}
                onClick={() => setActiveTab("products")}
              >
                Gestion des produits
              </button>
            </div>
            <div className="content-area">
              <div className="admin-content">
                <button onClick={handleMigration} className="migrate-button">
                  Migrer les produits vers Firestore
                </button>
                {renderContent()}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
