import { collection, getDocs, orderBy, query } from "firebase/firestore";
import React, { useContext, useEffect, useState } from "react";
import { AuthContext } from "../context/AuthContext";
import { db } from "../firebase";
import NavBar from "../NavBar/NavBar";
import "./Dashboard.css";

const Dashboard = () => {
  const { isAdmin } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);

  useEffect(() => {
    if (isAdmin) {
      fetchOrders();
    }
  }, [isAdmin]);

  const fetchOrders = async () => {
    try {
      const ordersRef = collection(db, "orders");
      const q = query(ordersRef, orderBy("date", "desc")); // Commandes les plus récentes d'abord
      const querySnapshot = await getDocs(q);

      const ordersData = querySnapshot.docs.map((doc) => ({
        id: doc.id,
        ...doc.data(),
        date: doc.data().date.toDate().toLocaleString(), // Convertir la date Firestore
      }));

      setOrders(ordersData);
    } catch (error) {
      console.error("Erreur lors de la récupération des commandes:", error);
    }
  };

  return (
    <div className="dashboard">
      <NavBar />
      <div className="dashboard-container">
        <h1>Dashboard</h1>
        {!isAdmin ? (
          <div className="unauthorized">Accès non autorisé</div>
        ) : (
          <div className="admin-content">
            <div className="orders-section">
              <h2>Historique des Commandes</h2>
              <div className="orders-list">
                {orders.length === 0 ? (
                  <p className="no-orders">Aucune commande trouvée</p>
                ) : (
                  <table className="orders-table">
                    <thead>
                      <tr>
                        <th>Date</th>
                        <th>Client</th>
                        <th>Produits</th>
                        <th>Total</th>
                        <th>Statut</th>
                      </tr>
                    </thead>
                    <tbody>
                      {orders.map((order) => (
                        <tr key={order.id}>
                          <td>{order.date}</td>
                          <td>{order.userEmail}</td>
                          <td>
                            <ul className="products-list">
                              {order.products.map((product, index) => (
                                <li key={index}>
                                  {product.name} x{product.quantity}
                                </li>
                              ))}
                            </ul>
                          </td>
                          <td>${order.total.toFixed(2)}</td>
                          <td>
                            <span
                              className={`status ${order.status.toLowerCase()}`}
                            >
                              {order.status}
                            </span>
                          </td>
                        </tr>
                      ))}
                    </tbody>
                  </table>
                )}
              </div>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
