import { collection, getDocs, query, where } from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { db } from "../firebase";
import NavBar from "../NavBar/NavBar";
import "./CategoryProducts.css";

const CategoryProducts = () => {
  const { category } = useParams();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        setLoading(true);
        const productsRef = collection(db, "products");

        // D'abord, vérifions si nous pouvons récupérer tous les produits
        const q = query(productsRef, where("categorie", "==", category));
        const querySnapshot = await getDocs(q);

        console.log("Catégorie recherchée:", category);
        console.log("Nombre de produits trouvés:", querySnapshot.size);

        const productsData = querySnapshot.docs.map((doc) => {
          const data = doc.data();
          return {
            id: doc.id,
            name: data.name || "",
            price: data.price || 0,
            description: data.description || "",
            categorie: data.categorie || "",
            imageUrl: data.imageUrl || "",
            server: data.server || "",
            subProduct: Array.isArray(data.subProduct) ? data.subProduct : [],
          };
        });

        // Trier les produits côté client pour le moment
        productsData.sort((a, b) => b.createdAt - a.createdAt);

        setProducts(productsData);
      } catch (error) {
        console.error("Erreur complète:", error);
      } finally {
        setLoading(false);
      }
    };

    if (category) {
      fetchProducts();
    }
  }, [category]);

  // Ajouter des logs pour le débogage
  console.log("État actuel:", {
    category,
    loading,
    productsCount: products.length,
  });

  return (
    <div>
      <NavBar />
      <div className="category-container">
        <h1>{category}</h1>
        {loading ? (
          <div className="loading">Chargement...</div>
        ) : products.length === 0 ? (
          <div className="no-products">
            Aucun produit trouvé dans cette catégorie
          </div>
        ) : (
          <div className="products-grid">
            {products.map((product) => (
              <div key={product.id} className="product-card">
                <div className="product-image">
                  <img
                    src={product.imageUrl || "/default-product.jpg"}
                    alt={product.name}
                  />
                </div>
                <div className="product-details">
                  <h2>{product.name}</h2>
                  <p className="price">${product.price}</p>
                  <p className="description">{product.description}</p>
                  {product.server && (
                    <p className="server">Serveur: {product.server}</p>
                  )}
                  {product.subProduct && product.subProduct.length > 0 && (
                    <div className="sub-products">
                      <h3>Versions disponibles:</h3>
                      <ul>
                        {product.subProduct.map((sub, index) => (
                          <li key={index}>
                            {sub.dlc}: ${sub.price}
                          </li>
                        ))}
                      </ul>
                    </div>
                  )}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default CategoryProducts;
