import { collection, getDocs, limit, query, where } from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { Helmet } from "react-helmet-async";
import { useNavigate, useParams } from "react-router-dom";
import { db } from "../firebase";
import NavBar from "../NavBar/NavBar";
import "./CategoryProducts.css";

// Liste blanche des catégories autorisées
const ALLOWED_CATEGORIES = ["electronics", "clothing", "books"];

const CategoryProducts = () => {
  const { category } = useParams();
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState(null);

  // Validation de l'URL
  const sanitizeImageUrl = (url) => {
    try {
      const validUrl = new URL(url);
      if (!["http:", "https:"].includes(validUrl.protocol)) {
        return "/default-product.jpg";
      }
      return url;
    } catch {
      return "/default-product.jpg";
    }
  };

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        // Validation stricte de la catégorie
        if (!ALLOWED_CATEGORIES.includes(category)) {
          navigate("/", { replace: true });
          return;
        }

        setLoading(true);
        const productsRef = collection(db, "products");
        const q = query(
          productsRef,
          where("categorie", "==", category),
          limit(50) // Limitation des résultats
        );

        const querySnapshot = await getDocs(q);
        const productsData = querySnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }));

        setProducts(productsData);
      } catch (error) {
        console.error("Erreur:", error);
        setError("Une erreur est survenue");
      } finally {
        setLoading(false);
      }
    };

    fetchProducts();
  }, [category, navigate]);

  const handleProductClick = (productId) => {
    navigate(`/product-details/${encodeURIComponent(productId)}`);
  };

  return (
    <div>
      <Helmet>
        <title>{category} - Produits</title>
        <meta
          http-equiv="Content-Security-Policy"
          content="default-src 'self'; img-src https: data:;"
        />
      </Helmet>

      <NavBar />

      <div className="category-container">
        <h1>{category}</h1>

        {error && <div className="error">Erreur de chargement des données</div>}

        {loading ? (
          <div className="loading">Chargement...</div>
        ) : products.length === 0 ? (
          <div className="no-products">Aucun produit trouvé</div>
        ) : (
          <div className="products-grid">
            {products.map((product) => (
              <div
                key={product.id}
                className="product-card"
                onClick={() => handleProductClick(product.id)}
                role="button"
                tabIndex={0}
                onKeyPress={(e) =>
                  e.key === "Enter" && handleProductClick(product.id)
                }
              >
                <div className="product-image">
                  <img
                    src={sanitizeImageUrl(product.imageUrl)}
                    alt={product.name}
                    crossOrigin="anonymous"
                  />
                </div>
                <div className="product-details">
                  <h2>{product.name}</h2>
                  <p className="price">${Number(product.price).toFixed(2)}</p>
                  <p className="description">
                    {product.description.substring(0, 100)}
                  </p>
                  {/* Reste du contenu */}
                </div>
              </div>
            ))}
          </div>
        )}
      </div>
    </div>
  );
};

export default CategoryProducts;
