import React, { useState, useEffect } from "react";
import { auth, db } from "../../firebase"; // db pour récupérer l'historique des commandes
import { updatePassword, signOut } from "firebase/auth";
import { collection, getDocs, query, where } from "firebase/firestore";
import { useNavigate } from "react-router-dom";

const Account = () => {
  const [user, setUser] = useState(null);
  const [activeSection, setActiveSection] = useState("info"); // Section active : "info", "password", "orders"
  const [newPassword, setNewPassword] = useState("");
  const [message, setMessage] = useState("");
  const [orders, setOrders] = useState([]);
  const navigate = useNavigate();

  // Récupère l'utilisateur actuel
  useEffect(() => {
    const unsubscribe = auth.onAuthStateChanged((currentUser) => {
      if (currentUser) {
        setUser(currentUser);
        fetchOrders(currentUser.uid); // Charge les commandes de l'utilisateur
      } else {
        navigate("/login"); // Redirige si non connecté
      }
    });

    return () => unsubscribe();
  }, [navigate]);

  // Récupère les commandes de l'utilisateur depuis Firestore
  const fetchOrders = async (uid) => {
    try {
      const ordersQuery = query(collection(db, "orders"), where("userId", "==", uid));
      const querySnapshot = await getDocs(ordersQuery);
      const ordersData = querySnapshot.docs.map((doc) => ({ id: doc.id, ...doc.data() }));
      setOrders(ordersData);
    } catch (error) {
      console.error("Erreur lors de la récupération des commandes :", error);
    }
  };

  // Gérer la modification du mot de passe
  const handlePasswordChange = async () => {
    if (!newPassword) {
      setMessage("Veuillez entrer un nouveau mot de passe.");
      return;
    }

    try {
      await updatePassword(auth.currentUser, newPassword);
      setMessage("Mot de passe mis à jour avec succès !");
      setNewPassword("");
    } catch (error) {
      setMessage("Erreur lors de la mise à jour du mot de passe : " + error.message);
    }
  };

  // Déconnexion
  const handleLogout = async () => {
    await signOut(auth);
    navigate("/login");
  };

  // Render des différentes sections
  const renderSection = () => {
    switch (activeSection) {
      case "info":
        return (
          <div>
            <h2>Informations du compte</h2>
            {user && (
              <div>
                <p><strong>Nom :</strong> {user.displayName || "Non défini"}</p>
                <p><strong>Email :</strong> {user.email}</p>
              </div>
            )}
          </div>
        );
      case "password":
        return (
          <div>
            <h2>Modifier le mot de passe</h2>
            <input
              type="password"
              placeholder="Nouveau mot de passe"
              value={newPassword}
              onChange={(e) => setNewPassword(e.target.value)}
            />
            <button onClick={handlePasswordChange}>Mettre à jour</button>
            {message && <p>{message}</p>}
          </div>
        );
      case "orders":
        return (
          <div>
            <h2>Historique des commandes</h2>
            {orders.length > 0 ? (
              <ul>
                {orders.map((order) => (
                  <li key={order.id}>
                    <p><strong>Commande #{order.id}</strong></p>
                    <p>Date : {new Date(order.date).toLocaleDateString()}</p>
                    <p>Total : {order.total} €</p>
                  </li>
                ))}
              </ul>
            ) : (
              <p>Aucune commande trouvée.</p>
            )}
          </div>
        );
      default:
        return <p>Section inconnue.</p>;
    }
  };

  return (
    <div className="account-container">
      <h1>Mon compte</h1>
      <div className="account-layout">
        {/* Menu latéral */}
        <div className="account-sidebar">
          <button onClick={() => setActiveSection("info")}>Informations du compte</button>
          <button onClick={() => setActiveSection("password")}>Modifier le mot de passe</button>
          <button onClick={() => setActiveSection("orders")}>Historique des commandes</button>
          <button onClick={handleLogout}>Se déconnecter</button>
        </div>

        {/* Contenu principal */}
        <div className="account-content">{renderSection()}</div>
      </div>
    </div>
  );
};

export default Account;
