import { addDoc, collection } from "firebase/firestore";
import React, { useEffect, useState } from "react";
import { fetchCategories } from "../../utils/categories";
import { db } from "../firebase";
import NavBar from "../NavBar/NavBar";
import "./ProductForm.css";

const ProductForm = () => {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [categorie, setCategorie] = useState("");
  const [imageUrl, setImageUrl] = useState("");
  const [server, setServer] = useState("");
  const [subProduct, setSubProduct] = useState([{ dlc: "", price: "" }]);
  const [categories, setCategories] = useState([]);
  const [accountDetails, setAccountDetails] = useState({
    email: "",
    password: "",
    recoveryEmail: "",
    additionalInfo: "",
  });

  // Charger les catégories au démarrage
  useEffect(() => {
    const loadCategories = async () => {
      const fetchedCategories = await fetchCategories();
      setCategories(fetchedCategories);
    };
    loadCategories();
  }, []);

  // Ajouter un sous-produit
  const addSubProduct = () => {
    setSubProduct([...subProduct, { dlc: "", price: "" }]);
  };

  // Supprimer un sous-produit
  const deleteSubProduct = (index) => {
    setSubProduct(subProduct.filter((_, i) => i !== index));
  };

  // Mettre à jour un sous-produit
  const updateSubProduct = (index, field, value) => {
    const updatedSubProduct = subProduct.map((sub, i) =>
      i === index ? { ...sub, [field]: value } : sub
    );
    setSubProduct(updatedSubProduct);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const productsRef = collection(db, "products");

      // Vérifier si la catégorie existe dans la liste des catégories
      const categoryExists = categories.some(
        (cat) => cat.name === categorie.trim()
      );
      if (!categoryExists) {
        throw new Error("Veuillez sélectionner une catégorie valide");
      }

      await addDoc(productsRef, {
        name,
        price: Number(price),
        description,
        categorie: categorie.trim(),
        imageUrl,
        server,
        subProduct,
        accountDetails,
        createdAt: new Date(),
      });

      // Réinitialisation du formulaire
      setName("");
      setPrice("");
      setCategorie("");
      setDescription("");
      setImageUrl("");
      setServer("");
      setSubProduct([{ dlc: "", price: "" }]);
      setAccountDetails({
        email: "",
        password: "",
        recoveryEmail: "",
        additionalInfo: "",
      });

      alert("Produit ajouté avec succès!");
    } catch (error) {
      console.error("Erreur lors de l'ajout du produit:", error);
      alert(error.message || "Erreur lors de l'ajout du produit");
    }
  };

  return (
    <>
      <NavBar />
      <h2>Add New Product</h2>
      <div className="container">
        <form onSubmit={handleSubmit} className="product-form">
          <div className="form-group">
            <label htmlFor="product-name">Name:</label>
            <input
              type="text"
              id="product-name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter product name"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="product-server">Server (optional):</label>
            <select
              id="product-server"
              value={server}
              onChange={(e) => setServer(e.target.value)}
            >
              <option value="">Select a server</option>
              <option value="America">America</option>
              <option value="Europe">Europe</option>
              <option value="Asia">Asia</option>
              <option value="Global">Global</option>
              <option value="Jp">Jp</option>
            </select>
          </div>

          <div className="form-group">
            <label htmlFor="product-price">Price:</label>
            <input
              type="number"
              id="product-price"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              placeholder="Enter price"
              min="0"
              step="0.01"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="product-categorie">Game:</label>
            <select
              id="product-categorie"
              value={categorie}
              onChange={(e) => setCategorie(e.target.value)}
              required
            >
              <option value="">Sélectionner un jeu</option>
              {categories.map((cat) => (
                <option key={cat.id} value={cat.name}>
                  {cat.name}
                </option>
              ))}
            </select>
          </div>

          {/* Section des versions (anciennement subProduct) */}
          <div className="form-group">
            <label>Versions:</label>
            {subProduct.map((sub, index) => (
              <div key={index} className="sub-product-form">
                <input
                  type="text"
                  placeholder="Version name"
                  value={sub.dlc}
                  onChange={(e) =>
                    updateSubProduct(index, "dlc", e.target.value)
                  }
                />
                <input
                  type="number"
                  placeholder="Version price"
                  value={sub.price}
                  onChange={(e) =>
                    updateSubProduct(index, "price", e.target.value)
                  }
                  min="0"
                  step="0.01"
                />
                <button type="button" onClick={() => deleteSubProduct(index)}>
                  Delete
                </button>
              </div>
            ))}
            <button type="button" onClick={addSubProduct}>
              Add Version
            </button>
          </div>

          <div className="form-group">
            <label htmlFor="product-image">Image URL:</label>
            <input
              type="url"
              id="product-image"
              value={imageUrl}
              onChange={(e) => setImageUrl(e.target.value)}
              placeholder="Enter image URL"
              required
            />
          </div>

          <div className="form-group">
            <label htmlFor="product-description">Description:</label>
            <textarea
              id="product-description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Enter product description"
              rows="4"
              required
            />
          </div>

          <div className="form-section">
            <h3>Détails du compte</h3>
            <input
              type="text"
              placeholder="Email du compte"
              value={accountDetails.email}
              onChange={(e) =>
                setAccountDetails({ ...accountDetails, email: e.target.value })
              }
            />
            <input
              type="text"
              placeholder="Mot de passe"
              value={accountDetails.password}
              onChange={(e) =>
                setAccountDetails({
                  ...accountDetails,
                  password: e.target.value,
                })
              }
            />
            <textarea
              placeholder="Informations supplémentaires"
              value={accountDetails.additionalInfo}
              onChange={(e) =>
                setAccountDetails({
                  ...accountDetails,
                  additionalInfo: e.target.value,
                })
              }
            />
          </div>

          <button type="submit" className="submit-button">
            Add Product
          </button>
        </form>
      </div>
    </>
  );
};

export default ProductForm;
