import axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import NavBar from "../NavBar/NavBar";
import "./ProductDetails.css";

const ProductDetails = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedSubProduct, setSelectedSubProduct] = useState(null);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3000/products/${productId}`
        );
        setProduct(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Erreur lors de la récupération du produit:", error);
        setLoading(false);
      }
    };

    fetchProduct();
  }, [productId]);

  const handleAddToCart = () => {
    const itemToAdd = {
      ...product,
      subProduct: selectedSubProduct
        ? [selectedSubProduct]
        : product.subProduct,
    };
    addToCart(itemToAdd);
    alert("Produit ajouté au panier !");
  };

  if (loading) {
    return <div className="loading">Chargement...</div>;
  }

  if (!product) {
    return <div>Produit non trouvé</div>;
  }

  return (
    <div className="product-details-page">
      <NavBar />
      <div className="product-details-container">
        <div className="product-image-section">
          <img src={product.imageUrl} alt={product.name} />
        </div>
        <div className="product-info-section">
          <h1>{product.name}</h1>
          <p className="price">{product.price} €</p>
          <div className="description">
            <h2>Description</h2>
            <p>{product.description}</p>
          </div>

          {product.subProduct && product.subProduct.length > 0 && (
            <div className="sub-products-section">
              <h2>Options disponibles</h2>
              <div className="sub-products-list">
                {product.subProduct.map((sub, index) => (
                  <div
                    key={index}
                    className={`sub-product-option ${
                      selectedSubProduct === sub ? "selected" : ""
                    }`}
                    onClick={() => setSelectedSubProduct(sub)}
                  >
                    <span>{sub.dlc}</span>
                    <span className="sub-price">{sub.price} €</span>
                  </div>
                ))}
              </div>
            </div>
          )}

          <button className="add-to-cart-btn" onClick={handleAddToCart}>
            Ajouter au panier
          </button>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
