import axios from "axios";
import React, { useEffect, useRef, useState } from "react";
import NavBar from "../NavBar/NavBar";
import "./ProductForm.css";

const ProductForm = () => {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [categorie, setCategorie] = useState("");
  const [subProduct, setSubProduct] = useState([{ dlc: "", price: "" }]);

  // État pour la suggestion de catégories
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const inputRef = useRef(null);

  // Liste initiale des catégories
  const initialCategories = [
    "Jujutsu Kaisen Phantom Parade",
    "Genshin Impact",
    "Honkai: Star Rail",
    "Fate/Grand Order",
    "Dragon Ball Legend",
    "Pokemon TCG Pocket",
    "Yu-Gi-Oh! Duel Links",
    "One Piece Bounty Rush",
    "Dokkan Battle",
  ];

  // Fonction de filtrage des suggestions
  const filterSuggestions = (input) => {
    if (!input) {
      setSuggestions([]);
      setShowSuggestions(false);
      return;
    }
    const filtered = initialCategories.filter((category) =>
      category.toLowerCase().includes(input.toLowerCase())
    );
    setSuggestions(filtered);
    setShowSuggestions(filtered.length > 0);
  };

  // Gérer le changement de catégorie (saisie utilisateur)
  const handleCategorieChange = (e) => {
    const value = e.target.value;
    setCategorie(value);
    filterSuggestions(value);
  };

  // Sélectionner une suggestion
  const handleSelectSuggestion = (suggestion) => {
    setCategorie(suggestion);
    setShowSuggestions(false);
  };

  // Fermer les suggestions quand on clique ailleurs
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (inputRef.current && !inputRef.current.contains(event.target)) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Ajouter un sous-produit
  const addSubProduct = () => {
    setSubProduct([...subProduct, { dlc: "", price: "" }]);
  };

  // Supprimer un sous-produit
  const deleteSubProduct = (index) => {
    setSubProduct(subProduct.filter((_, i) => i !== index));
  };

  // Mettre à jour un sous-produit
  const updateSubProduct = (index, field, value) => {
    const updatedSubProduct = subProduct.map((sub, i) =>
      i === index ? { ...sub, [field]: value } : sub
    );
    setSubProduct(updatedSubProduct);
  };

  // Soumettre le formulaire
  const handleSubmit = async (e) => {
    e.preventDefault(); // Prevents the default form submission behavior

    const newProduct = {
      name,
      price: parseFloat(price), // Ensures price is a number
      description,
      categorie,
      subProduct,
    };

    try {
      const response = await axios.post(
        "http://localhost:3000/products",
        newProduct
      );

      if (response.status === 201) {
        alert("Produit ajouté avec succès !");
        // Reset the form after successful submission
        setName("");
        setPrice("");
        setCategorie("");
        setDescription("");
        setSubProduct([{ dlc: "", price: "" }]);
      }
    } catch (error) {
      console.error("Erreur lors de l'ajout du produit :", error);
    }
  };

  return (
    <>
      <NavBar />

      <h2> Product Form</h2>
      <div className="container">
        <form onSubmit={handleSubmit} className="product-form">
          {/* Nom */}
          <div className="form-group">
            <label htmlFor="product-name">Nom : </label>
            <input
              type="text"
              id="product-name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter the name of the product"
              required
            />
          </div>

          {/* Prix */}
          <div className="form-group">
            <label htmlFor="product-price">Price : </label>
            <input
              type="number"
              id="product-price"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              placeholder="Enter the price of the product"
              min="0"
              step={"0.1"}
              required
            />
          </div>

          {/* Catégorie avec suggestions */}
          <div className="form-group" ref={inputRef}>
            <label htmlFor="product-categorie">Jeu :</label>
            <input
              type="text"
              id="product-categorie"
              value={categorie}
              onChange={handleCategorieChange}
              placeholder="Entrez ou choisissez un jeu"
              required
            />

            {/* Affichage des suggestions */}
            {showSuggestions && suggestions.length > 0 && (
              <div className="suggestions show">
                <ul>
                  {suggestions.map((suggestion, index) => (
                    <li
                      key={index}
                      onClick={() => handleSelectSuggestion(suggestion)}
                    >
                      {suggestion}
                    </li>
                  ))}
                </ul>
              </div>
            )}
          </div>

          {/* Sous-produits */}
          <div className="form-group">
            <label> Sub product of the product :</label>
            {subProduct.map((sub, index) => (
              <div key={index} className="subAccount-row">
                <input
                  type="text"
                  placeholder="SubACCOUNT"
                  value={sub.dlc}
                  onChange={(e) =>
                    updateSubProduct(index, "dlc", e.target.value)
                  }
                />
                <input
                  type="number"
                  placeholder="Price (€)"
                  value={sub.price}
                  onChange={(e) =>
                    updateSubProduct(index, "price", e.target.value)
                  }
                  min="0"
                  step="0.1"
                />
                <button type="button" onClick={() => deleteSubProduct(index)}>
                  Delete
                </button>
              </div>
            ))}
            <button type="button" onClick={addSubProduct}>
              Add a Sub Product
            </button>
          </div>

          {/* Description */}
          <div className="form-group">
            <label htmlFor="product-description">Description :</label>
            <textarea
              id="product-description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Enter a description for the product"
              rows="4"
              required
            />
          </div>

          <button type="submit" className="submit-button">
            Ajouter le produit
          </button>
        </form>
      </div>
    </>
  );
};

export default ProductForm;
