import axios from "axios";
import React, { useEffect, useRef, useState } from "react";
import NavBar from "../NavBar/NavBar";
import "./ProductForm.css";

const ProductForm = () => {
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [categorie, setCategorie] = useState("");
  const [subProduct, setSubProduct] = useState([{ dlc: "", price: "" }]);

  // État pour la suggestion de catégories
  const [showSuggestions, setShowSuggestions] = useState(false);
  const [suggestions, setSuggestions] = useState([]);
  const inputRef = useRef(null);

  //  Add a sub Product

  const addSubProduct = () => {
    setSubProduct([...subProduct, { dlc: "", price: "" }]);
  };

  // Liste initiale des catégories
  const initialCategories = [
    "Jujutsu Kaisen Phantom Parade",
    "Genshin Impact",
    "Honkai: Star Rail",
    "Fate/Grand Order",
    "Dragon Ball Legend",
    "Pokemon TCG Pocket",
    "Yu-Gi-Oh! Duel Links",
    "One Piece Bounty Rush",
    "Dokkan Battle",
  ];

  // Filtrer les suggestions basées sur l'entrée
  const filterSuggestions = (input) => {
    const filtered = initialCategories.filter((category) =>
      category.toLowerCase().includes(input.toLowerCase())
    );
    setSuggestions(filtered);
    setShowSuggestions(true);
  };

  // Gérer le changement de catégorie
  const handleCategorieChange = (e) => {
    const value = e.target.value;
    setCategorie(value);
    filterSuggestions(value);
  };

  // Sélectionner une suggestion
  const handleSelectSuggestion = (suggestion) => {
    setCategorie(suggestion);
    setShowSuggestions(false);
  };

  // Fermer les suggestions quand on clique ailleurs
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (inputRef.current && !inputRef.current.contains(event.target)) {
        setShowSuggestions(false);
      }
    };

    document.addEventListener("mousedown", handleClickOutside);
    return () => {
      document.removeEventListener("mousedown", handleClickOutside);
    };
  }, []);

  // Delete a sub product
  const deleteSubProduct = (index) => {
    setSubProduct(subProduct.filter((_, i) => i !== index));
  };

  //   Update a sub product
  const updateSubProduct = (index, field, value) => {
    const newSuBProduct = subProduct.map((subProduct, i) =>
      i === index ? { ...subProduct, [field]: value } : subProduct
    );
  };
  // Submit the form

  const handleSubmit = async (e) => {
    e.preventDefault();
    const newProduct = { name, price, description, categorie, subProduct };

    try {
      const response = await axios.post(
        "http://localhost:3000/products",
        newProduct
      );

      if (response.status === 201) {
        alert("Produit ajouté avec succès !");
        // Reset the form
        setName("");
        setPrice("");
        setCategorie("");
        setDescription("");
        setSubProduct([{ dlc: "", price: "" }]);
      }
    } catch (error) {
      console.error("Error with the add of the product:", error.message);
    }
  };

  return (
    <>
      <NavBar />

      {/* Form */}
      <h2> Product form</h2>
      <div className="container">
        <form onSubmit="{handleSubmit}" className="product-form">
          {/* Name */}
          <div className="form-group">
            <label htmlFor="product-name">Nom : </label>
            <input
              type="text"
              id="product-name"
              value={name}
              onChange={(e) => setName(e.target.value)}
              placeholder="Enter the name of the product"
              required
            />
          </div>
          {/* Price */}
          <div className="form-group">
            <label htmlFor="product-price">Price : </label>
            <input
              type="number"
              id="product-price"
              value={price}
              onChange={(e) => setPrice(e.target.value)}
              placeholder="Enter the price of the product"
              min="0"
              step={"0.1"}
              required
            />
          </div>
          {/* <div className="form-group">
            <label htmlFor="product-categorie">Jeux :</label>
            <select
              id="product-categorie"
              value={categorie}
              onChange={(e) => setCategorie(e.target.value)}
              required
            >
              <option value="">-- Choisir un jeu --</option>
              <option value="jujutsu">Jujutsu Kaisen Phantom Parade</option>
              <option value="genshin">Genshin Impact</option>
              <option value="honkai">Honkai: Star Rail</option>
              <option value="fgo">Fate/Grand Order</option>
              <option value="dbl">Dragon Ball Legend</option>
            </select>
          </div> */}
          <div className="form-group" ref={inputRef}>
            <label htmlFor="product-categorie">Jeu :</label>
            <input
              type="text"
              id="product-categorie"
              value={categorie}
              onChange={handleCategorieChange}
              placeholder="Entrez ou choisissez un jeu"
              required
            />

            {/* Liste des suggestions */}
            {showSuggestions &&
              suggestions.length > 0 &&
              show(
                <div className="suggestions">
                  <ul className="py-1">
                    {suggestions.map((suggestion, index) => (
                      <li
                        key={index}
                        onClick={() => handleSelectSuggestion(suggestion)}
                      >
                        {suggestion}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
          </div>

          {/* Ajoute des divers variante du produit */}
          <div className="form-group">
            <label htmlFor="product-subproduct">
              {" "}
              Sub product of the product :
            </label>
            {subProduct.map((subProduct, index) => (
              <div key={index} className="subAccount-row">
                <input
                  type="text"
                  placeholder="SubACCOUNT"
                  value={subProduct.dlc}
                  onChange={(e) =>
                    updateSubProduct(index, "dlc", e.target.value)
                  }
                  optional
                />
                <input
                  type="number"
                  placeholder="Price (€)"
                  value={subProduct.price}
                  onChange={(e) =>
                    updateSubProduct(index, "price", e.target.value)
                  }
                  min="0"
                  step="0.1"
                  optional
                />
                <button type="button" onClick={() => deleteSubProduct(index)}>
                  Delete
                </button>
              </div>
            ))}

            <button type="button" onClick={addSubProduct}>
              Add an Sub Product
            </button>
          </div>
          <div className="form-group">
            <label htmlFor="product-description">Description :</label>
            <textarea
              id="product-description"
              value={description}
              onChange={(e) => setDescription(e.target.value)}
              placeholder="Entrez une description pour le produit"
              rows="4"
              required
            />
          </div>

          <button type="submit" className="submit-button">
            Ajouter le produit
          </button>
        </form>
      </div>
    </>
  );
};

export default ProductForm;
