import axios from "axios";
import React, { useEffect, useState } from "react";
import { useNavigate, useParams } from "react-router-dom";
import NavBar from "../NavBar/NavBar";
import "./CategoryProducts.css";

const CategoryProducts = () => {
  const { categoryName } = useParams();
  const navigate = useNavigate();
  const [products, setProducts] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sortBy, setSortBy] = useState("newest"); // Default sorting

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await axios.get(
          `http://localhost:3000/products/category/${categoryName}`
        );
        setProducts(response.data);
        setLoading(false);
      } catch (error) {
        console.error("Error fetching products:", error);
        setLoading(false);
      }
    };

    fetchProducts();
  }, [categoryName]);

  const handleProductClick = (productId) => {
    navigate(`/product/${productId}`);
  };

  const getSortedProducts = () => {
    const sortedProducts = [...products];

    switch (sortBy) {
      case "newest":
        return sortedProducts.sort(
          (a, b) => new Date(b.createdAt) - new Date(a.createdAt)
        );
      case "oldest":
        return sortedProducts.sort(
          (a, b) => new Date(a.createdAt) - new Date(b.createdAt)
        );
      case "priceAsc":
        return sortedProducts.sort(
          (a, b) => parseFloat(a.price) - parseFloat(b.price)
        );
      case "priceDesc":
        return sortedProducts.sort(
          (a, b) => parseFloat(b.price) - parseFloat(a.price)
        );
      case "nameAsc":
        return sortedProducts.sort((a, b) => a.name.localeCompare(b.name));
      case "nameDesc":
        return sortedProducts.sort((a, b) => b.name.localeCompare(a.name));
      case "mostSold":
        return sortedProducts.sort(
          (a, b) => (b.salesCount || 0) - (a.salesCount || 0)
        );
      default:
        return sortedProducts;
    }
  };

  if (loading) {
    return <div className="loading">Loading...</div>;
  }

  return (
    <div className="category-products">
      <NavBar />
      <div className="category-header">
        <h1>{categoryName}</h1>
        <div className="category-controls">
          <div className="sort-controls">
            <label htmlFor="sort">Sort by:</label>
            <select
              id="sort"
              value={sortBy}
              onChange={(e) => setSortBy(e.target.value)}
              className="sort-select"
            >
              <option value="newest">Newest First</option>
              <option value="oldest">Oldest First</option>
              <option value="priceAsc">Price: Low to High</option>
              <option value="priceDesc">Price: High to Low</option>
              <option value="nameAsc">Name: A to Z</option>
              <option value="nameDesc">Name: Z to A</option>
              <option value="mostSold">Most Popular</option>
            </select>
          </div>
        </div>
      </div>

      <div className="products-grid">
        {getSortedProducts().map((product) => (
          <div
            key={product.id}
            className="product-card"
            onClick={() => handleProductClick(product.id)}
            style={{ cursor: "pointer" }}
          >
            <div className="product-image">
              <img
                src={product.imageUrl || "/default-product.jpg"}
                alt={product.name}
              />
            </div>
            <div className="product-details">
              <h2>{product.name}</h2>
              <p className="price">${product.price}</p>
              {product.subProduct && product.subProduct.length > 0 && (
                <div className="sub-products">
                  <h3>Associated Products:</h3>
                  <ul>
                    {product.subProduct.map((sub, index) => (
                      <li key={index}>
                        {sub.dlc} - ${sub.price}
                      </li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default CategoryProducts;
