import { PayPalButtons } from "@paypal/react-paypal-js";
import { addDoc, collection, doc, getDoc } from "firebase/firestore";
import React, { useContext, useEffect, useState } from "react";
import { useParams } from "react-router-dom";
import { CartContext } from "../context/CartContext";
import { db } from "../firebase";
import NavBar from "../NavBar/NavBar";
import "./ProductDetails.css";

const ProductDetails = () => {
  const { productId } = useParams();
  const [product, setProduct] = useState(null);
  const [loading, setLoading] = useState(true);
  const [selectedSubProduct, setSelectedSubProduct] = useState(null);
  const { addToCart } = useContext(CartContext);

  useEffect(() => {
    const fetchProduct = async () => {
      try {
        console.log("Tentative de récupération du produit avec ID:", productId);

        const productRef = doc(db, "products", productId);
        const productSnap = await getDoc(productRef);

        console.log("Document existe ?", productSnap.exists());
        console.log("Données du document:", productSnap.data());

        if (productSnap.exists()) {
          const productData = {
            id: productSnap.id,
            ...productSnap.data(),
          };
          console.log("Données formatées:", productData);
          setProduct(productData);
        } else {
          console.log("Aucun document trouvé avec l'ID:", productId);
        }
      } catch (error) {
        console.error("Erreur lors de la récupération:", error);
      } finally {
        setLoading(false);
      }
    };

    if (productId) {
      fetchProduct();
    }
  }, [productId]);

  const handleAddToCart = () => {
    const itemToAdd = {
      ...product,
      subProduct: selectedSubProduct
        ? [selectedSubProduct]
        : product.subProduct,
    };
    addToCart(itemToAdd);
    alert("Product added to cart!");
  };

  const handleBuyNow = () => {
    // Trouver et cliquer sur le bouton PayPal
    const paypalButton = document.querySelector(".paypal-button");
    if (paypalButton) {
      paypalButton.click();
    }
  };

  const createPaypalOrder = (data, actions) => {
    return actions.order.create({
      purchase_units: [
        {
          description: product.name,
          amount: {
            value: selectedSubProduct
              ? selectedSubProduct.price
              : product.price,
          },
          payee: {
            email_address: "ayoube.manjal.pro@gmail.com",
          },
        },
      ],
    });
  };

  if (loading) {
    return <div className="loading">Chargement...</div>;
  }

  if (!product) {
    return <div>Produit non trouvé</div>;
  }

  return (
    <div className="product-details-page">
      <NavBar />
      <div className="product-details-container">
        <div className="product-image-section">
          <img src={product.imageUrl} alt={product.name} />
        </div>
        <div className="product-info-section">
          <h1>{product.name}</h1>
          <p className="price">${product.price}USD</p>

          {/* Sélection du serveur si disponible */}
          {product.server && (
            <div className="server-selection">
              <label>Server</label>
              <select className="server-select">
                <option value={product.server}>{product.server}</option>
              </select>
            </div>
          )}

          {/* Sélection de la version */}
          {product.subProduct && product.subProduct.length > 0 && (
            <div className="version-selection">
              <label>Version</label>
              <select
                className="version-select"
                onChange={(e) => {
                  const selected = product.subProduct.find(
                    (sub) => sub.dlc === e.target.value
                  );
                  setSelectedSubProduct(selected);
                }}
              >
                <option value="">Select version</option>
                {product.subProduct.map((sub, index) => (
                  <option key={index} value={sub.dlc}>
                    {sub.dlc}
                  </option>
                ))}
              </select>
            </div>
          )}

          {/* Quantité */}
          <div className="quantity-selection">
            <label>Quantity</label>
            <input type="number" defaultValue="1" min="1" />
          </div>

          <div className="payment-options">
            <button className="buy-now-btn" onClick={handleBuyNow}>
              Buy it now
            </button>

            <div style={{ display: "none" }}>
              <PayPalButtons
                className="paypal-button"
                createOrder={createPaypalOrder}
                onApprove={async (data, actions) => {
                  try {
                    const details = await actions.order.capture();
                    console.log("Paiement réussi:", details);

                    // Créer la commande dans Firestore
                    const orderRef = collection(db, "orders");
                    await addDoc(orderRef, {
                      date: new Date(),
                      userEmail: details.payer.email_address,
                      products: [
                        {
                          id: product.id,
                          name: product.name,
                          price: selectedSubProduct
                            ? selectedSubProduct.price
                            : product.price,
                          quantity: 1,
                          version: selectedSubProduct
                            ? selectedSubProduct.dlc
                            : null,
                        },
                      ],
                      total: selectedSubProduct
                        ? selectedSubProduct.price
                        : product.price,
                      status: "completed",
                      paypalOrderId: details.id,
                      payerInfo: {
                        name: `${details.payer.name.given_name} ${details.payer.name.surname}`,
                        email: details.payer.email_address,
                      },
                    });

                    alert("Commande effectuée avec succès!");
                  } catch (error) {
                    console.error(
                      "Erreur lors de la création de la commande:",
                      error
                    );
                    alert("Erreur lors de la finalisation de la commande");
                  }
                }}
              />
            </div>

            <button className="add-to-cart-btn" onClick={handleAddToCart}>
              Add to Cart
            </button>
          </div>

          <div className="description">
            <h2>Description</h2>
            <p>{product.description}</p>
          </div>
        </div>
      </div>
    </div>
  );
};

export default ProductDetails;
