import {
  addDoc,
  collection,
  deleteDoc,
  doc,
  getDocs,
  orderBy,
  query,
  where,
  writeBatch,
} from "firebase/firestore";
import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { AuthContext } from "../context/AuthContext";
import { db } from "../firebase";
import NavBar from "../NavBar/NavBar";
import "./Dashboard.css";

const Dashboard = () => {
  const { isAdmin } = useContext(AuthContext);
  const [orders, setOrders] = useState([]);
  const [products, setProducts] = useState([]);
  const [accounts, setAccounts] = useState([]);
  const [dashboardCategories, setDashboardCategories] = useState([]);
  const [newCategory, setNewCategory] = useState("");
  const [selectedProductId, setSelectedProductId] = useState(null);
  const [activeTab, setActiveTab] = useState("products");
  const navigate = useNavigate();

  useEffect(() => {
    if (isAdmin) {
      if (activeTab === "orders") fetchOrders();
      else if (activeTab === "products") fetchProducts();
      else if (activeTab === "categories") loadCategories();
      else if (activeTab === "accounts" && selectedProductId)
        fetchAccounts(selectedProductId);
    }
  }, [isAdmin, activeTab, selectedProductId]);

  // ðŸ”¹ RÃ©cupÃ©rer les commandes
  const fetchOrders = async () => {
    try {
      const q = query(collection(db, "orders"), orderBy("date", "desc"));
      const querySnapshot = await getDocs(q);
      setOrders(
        querySnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
          date: doc.data().date.toDate().toLocaleString(),
        }))
      );
    } catch (error) {
      console.error("Erreur lors de la rÃ©cupÃ©ration des commandes:", error);
    }
  };

  // ðŸ”¹ RÃ©cupÃ©rer les produits
  const fetchProducts = async () => {
    try {
      const querySnapshot = await getDocs(collection(db, "products"));
      setProducts(
        querySnapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }))
      );
    } catch (error) {
      console.error("Erreur lors de la rÃ©cupÃ©ration des produits:", error);
    }
  };

  // ðŸ”¹ RÃ©cupÃ©rer les comptes liÃ©s Ã  un produit
  const fetchAccounts = async (productId) => {
    try {
      if (!productId) return;
      const q = query(
        collection(db, "accounts"),
        where("productId", "==", productId),
        orderBy("createdAt", "desc")
      );
      const snapshot = await getDocs(q);
      setAccounts(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
          createdAt: doc.data().createdAt?.toDate().toLocaleString(),
        }))
      );
    } catch (error) {
      console.error("Erreur lors du chargement des comptes:", error);
    }
  };

  // ðŸ”¹ Charger les catÃ©gories
  const loadCategories = async () => {
    try {
      const snapshot = await getDocs(collection(db, "categories"));
      setDashboardCategories(
        snapshot.docs.map((doc) => ({
          id: doc.id,
          ...doc.data(),
        }))
      );
    } catch (error) {
      console.error("Erreur lors du chargement des catÃ©gories:", error);
    }
  };

  // ðŸ”¹ Ajouter une catÃ©gorie
  const handleAddCategory = async (e) => {
    e.preventDefault();
    if (!newCategory.trim()) return;
    try {
      await addDoc(collection(db, "categories"), {
        name: newCategory.trim(),
        createdAt: new Date(),
      });
      setNewCategory("");
      loadCategories();
    } catch (error) {
      console.error("Erreur lors de l'ajout de la catÃ©gorie:", error);
    }
  };

  // ðŸ”¹ Supprimer une catÃ©gorie
  const handleDeleteCategory = async (categoryId) => {
    if (
      window.confirm("ÃŠtes-vous sÃ»r de vouloir supprimer cette catÃ©gorie ?")
    ) {
      try {
        await deleteDoc(doc(db, "categories", categoryId));
        loadCategories();
      } catch (error) {
        console.error("Erreur lors de la suppression de la catÃ©gorie:", error);
      }
    }
  };

  // ðŸ”¹ Supprimer un produit et ses comptes
  const handleDelete = async (productId) => {
    if (!productId || !window.confirm("Voulez-vous supprimer ce produit ?"))
      return;
    try {
      const q = query(
        collection(db, "accounts"),
        where("productId", "==", productId)
      );
      const accountsSnap = await getDocs(q);
      const batch = writeBatch(db);
      accountsSnap.docs.forEach((doc) => batch.delete(doc.ref));
      await batch.commit();
      await deleteDoc(doc(db, "products", productId));
      fetchProducts();
    } catch (error) {
      console.error("Erreur lors de la suppression:", error);
    }
  };

  // ðŸ”¹ Modifier un produit
  const handleEdit = (productId) => navigate(`/edit-product/${productId}`);

  return (
    <div className="dashboard">
      <NavBar />
      <div className="dashboard-container">
        <h1>Dashboard</h1>
        {!isAdmin ? (
          <div className="unauthorized">AccÃ¨s non autorisÃ©</div>
        ) : (
          <div className="dashboard-layout">
            <div className="sidebar">
              <button
                className={`tab-button ${
                  activeTab === "orders" ? "active" : ""
                }`}
                onClick={() => setActiveTab("orders")}
              >
                Commandes
              </button>
              <button
                className={`tab-button ${
                  activeTab === "products" ? "active" : ""
                }`}
                onClick={() => setActiveTab("products")}
              >
                Produits
              </button>
              <button
                className={`tab-button ${
                  activeTab === "categories" ? "active" : ""
                }`}
                onClick={() => setActiveTab("categories")}
              >
                CatÃ©gories
              </button>
              <button
                className={`tab-button ${
                  activeTab === "accounts" ? "active" : ""
                }`}
                onClick={() => setActiveTab("accounts")}
              >
                Comptes
              </button>
            </div>
            <div className="content-area">
              {activeTab === "products" && (
                <div className="products-section">
                  <h2>Produits</h2>
                  <div className="products-grid">
                    {products.map((product) => (
                      <div
                        key={product.id}
                        className="product-card"
                        onClick={() => {
                          setSelectedProductId(product.id);
                          fetchAccounts(product.id);
                        }}
                      >
                        <h3>{product.name}</h3>
                        <p>{product.price}â‚¬</p>
                        <button onClick={() => handleEdit(product.id)}>
                          Modifier
                        </button>
                        <button onClick={() => handleDelete(product.id)}>
                          Supprimer
                        </button>
                      </div>
                    ))}
                  </div>
                </div>
              )}
              {activeTab === "accounts" && selectedProductId && (
                <div className="accounts-section">
                  <h2>Comptes disponibles</h2>
                  <ul>
                    {accounts.map((account) => (
                      <li key={account.id}>{account.email}</li>
                    ))}
                  </ul>
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default Dashboard;
