import React, { useState } from "react";
import axios from "axios";
import '/Users/Berud/Desktop/test/frontend/src/index.css';
import './ProductForm.css';
import { FaUser, FaLock, FaPlus } from "react-icons/fa";
import { Link } from "react-router-dom";


const ProductForm = () => {
  // Ã‰tats pour gÃ©rer les champs du formulaire
  const [name, setName] = useState("");
  const [price, setPrice] = useState("");
  const [description, setDescription] = useState("");
  const [categorie, setCategorie] = useState("");
  const [subAccount, setSubAccount] = useState([{dlc: "", price: ""}]);

  // Fonction pour ajouter des sous produit

  // ajouter un sub account
  const addSubAccount = () =>{ 
    setSubAccount([...subAccount, { dlc:"", price: ""}]);
  };
  // supprimer un subAccount
  const removeSubAccount = (index) => {
    const upadtedSubAccount = subAccount.filter((_, i) => i !== index);
    setSubAccount(upadtedSubAccount);
  }

  // Mettre a jour un sub Account  
  const upadtedSubAccount = (index, field, value) => {
    const upadtedSubAccount = subAccount.map((subAccount, i) =>
      i == index ? {...subAccount, [field]: value} : subAccount
    );
    setSubAccount(upadtedSubAccount);
  };

  // Fonction de soumission du formulaire
  const handleSubmit = async (e) => {
    e.preventDefault(); // EmpÃªche le rechargement de la page
    const newProduct = { name, price, categorie, description, subAccount };

    try {
      const response = await axios.post("http://localhost:3000/products", newProduct);

      if (response.status === 201) {
        alert("Produit ajoutÃ© avec succÃ¨s !");
        // RÃ©initialise les champs du formulaire
        setName("");
        setPrice("");
        setCategorie("");
        setDescription("");
        setSubAccount([{dlc: "", price: ""}]);
      }
    } catch (error) {
      console.error("Erreur lors de l'ajout du produit :", error.message);
    }
  };

  return (
  <>
  {/* En tete du site  */}
  <header>   
        <nav>
            <div className='logo'>Gameshop</div>
            <ul className='nav-links'>
                <li><Link to="/">Acceuil</Link></li>   
                <li className='UserLogin'><Link to="/login"> <FaUser/></Link></li>
                <li className='Product-list'><Link to ="/product-form"><FaPlus /></Link></li>
                <div className='cart-icon'>
                    <span className='cart-count'>0</span>
                    ðŸ›’
                </div>          
            </ul>
        </nav>
    </header>
  <h2>Product Form</h2>
  {/* nom du produit */}
    <form onSubmit={handleSubmit} className="product-form">
      <div className="form-group">
        <label htmlFor="product-name">Nom :</label>
        <input
          type="text"
          id="product-name"
          value={name}
          onChange={(e) => setName(e.target.value)}
          placeholder="Entrez le nom du produit"
          required
        />
      </div>
      {/* prix du produit */}
      <div className="form-group">
        <label htmlFor="product-price">Prix (â‚¬) :</label>
        <input
          type="number"
          id="product-price"
          value={price}
          onChange={(e) => setPrice(e.target.value)}
          placeholder="Entrez le prix du produit"
          min="0"
          step="0.01"
          required
        />
      </div>
      {/* Categorie de jeu  */}
      <div className="form-group">
        <label htmlFor="product-categorie">Jeux :</label>
        <select
        id="product-categorie"
        value={categorie}
        onChange={(e) => setCategorie(e.target.value)}
        required
        >
          <option value="">-- Choisir un jeu --</option>
          <option value="jujutsu">Jujutsu Kaisen Phantom Parade</option>
          <option value="genshin">Genshin Impact</option>
          <option value="honkai">Honkai: Star Rail</option>
          <option value="fgo">Fate/Grand Order</option>
          <option value="dbl">Dragon Ball Legend</option>
      </select>
    </div>
      
      {/* Ajoute des divers variante du produit */}
      <div className="form-group">
        <label htmlFor="product-subproduct"> Sub product of the product :</label>
        {subAccount.map((subAccount, index) => (
          <div key={index} className="subAccount-row">
            <input 
              type="text"
              placeholder="SubACCOUNT"
              value={subAccount.dlc}
              onChange={(e) => upadtedSubAccount(index, "dlc", e.target.value)}
              optional
            />
            <input 
              type="number"
              placeholder="Price (â‚¬)"
              value={subAccount.price}
              onChange={(e) => upadtedSubAccount(index, "price", e.target.value)} 
              min = "0"
              step = "0.1"
              optional  
            />
            <button type="button" onClick={() => removeSubAccount(index)}>Delete</button>
          </div>
        ))}

        <button type="button" onClick={addSubAccount}>Add an Sub Product</button>
      </div>
      
      
    

      {/* description du produit */}
    <div className="form-group">
      <label htmlFor="product-description">Description :</label>
        <textarea
          id="product-description"
          value={description}
          onChange={(e) => setDescription(e.target.value)}
          placeholder="Entrez une description pour le produit"
          rows="4"
          required
        />
      </div>



      <button type="submit" className="submit-button">Ajouter le produit</button>
    </form>
    </>
  );
};

export default ProductForm;
